// src/pages/SystemStatus.js
import React, { useEffect, useState } from 'react';

export default function SystemStatus() {
  const [status, setStatus] = useState(null);

  useEffect(() => {
    fetch('https://…/api/status')
      .then(r => r.json())
      .then(setStatus)
      .catch(console.error);
  }, []);

  if (!status) return <p>Loading…</p>;

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">System Status</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white p-4 rounded shadow">
          <p className="text-gray-500">API Uptime</p>
          <p className="text-xl font-semibold">{status.uptimeFormatted}</p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <p className="text-gray-500">DB Connected</p>
          <p className={`font-semibold ${status.dbHealthy ? 'text-green-600' : 'text-red-600'}`}>
            {status.dbHealthy ? 'OK' : 'Down'}
          </p>
        </div>
        <div className="bg-white p-4 rounded shadow">
          <p className="text-gray-500">Version</p>
          <p className="text-xl font-semibold">{status.version}</p>
        </div>
      </div>
    </div>
  );
}
