import { useEffect, useState, useCallback } from 'react';

function formatUptime(seconds) {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor((seconds % 3600) / 60);
  return `${hours}h ${minutes}m`;
}

export function useStats() {
  const [stats, setStats] = useState({
    users: null,
    projects: null,
    uptime: null,
    systemStatus: null,
  });

  const fetchStats = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const [usersRes, projectRes, statusRes] = await Promise.all([
        fetch('https://forum-gen-backend-production.up.railway.app/api/users/count', {
          headers: { Authorization: `Bearer ${token}` },
        }),
        fetch('https://forum-gen-backend-production.up.railway.app/api/projects/count', {
          headers: { Authorization: `Bearer ${token}` },
        }),
        fetch('https://forum-gen-backend-production.up.railway.app/api/status', {
          headers: { Authorization: `Bearer ${token}` },
        }),
      ]);

      if (!usersRes.ok || !projectRes.ok || !statusRes.ok) {
        throw new Error('One or more API calls failed');
      }

      const { count: users } = await usersRes.json();
      const { count: projects } = await projectRes.json();
      const status = await statusRes.json();
      const uptimeFormatted = formatUptime(status.uptime);

      setStats({
        users,
        projects,
        uptime: uptimeFormatted,
        systemStatus: status.dbHealthy ? 'Operational' : 'Down',
      });
    } catch (err) {
      console.error('Failed to fetch stats:', err);
      setStats({ users: '-', projects: '-', uptime: '-', systemStatus: 'Down' });
    }
  }, []);

  useEffect(() => { fetchStats(); }, [fetchStats]);

  return { ...stats, refresh: fetchStats };
}
