import { useEffect, useState } from 'react';

function formatUptime(seconds) {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    return `${hours}h ${minutes}m`;
  }

export function useStats() {
  const [stats, setStats] = useState({
    users: null,
    forums: null,
    uptime: null,
    systemStatus: null,
  });

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const [usersRes, forumsRes, uptimeRes] = await Promise.all([
          fetch('https://forum-gen-backend-production.up.railway.app/api/users'),
          fetch('https://forum-gen-backend-production.up.railway.app/api/forums'),
          fetch('https://forum-gen-backend-production.up.railway.app/api/status'), // You'll need to build this route
        ]);

        const uptimeInSeconds = status?.uptime ?? 0;
        const uptimeFormatted = formatUptime(uptimeInSeconds);

        const users = await usersRes.json();
        const forums = await forumsRes.json();
        const status = await uptimeRes.json();

        setStats({
          users: users.length,
          forums: forums.length,
          uptime: status?.uptime ?? null,
          systemStatus: status?.healthy ? 'Operational' : 'Down',
        });
      } catch (err) {
        console.error('Failed to fetch stats:', err);
        setStats({ users: '-', forums: '-', uptime: '-', systemStatus: 'Down' });
      }
    };

    fetchStats();
  }, []);

  return stats;
}
