import React, { useState } from 'react';
import { useStats } from '../hooks/useStats';
import { usePaginatedActivity } from '../hooks/usePaginatedActivity';
import {
  UserGroupIcon,
  ServerStackIcon,
  ClockIcon,
  ChartBarIcon,
  UserPlusIcon,
  EyeIcon,
  DocumentDuplicateIcon,
} from '@heroicons/react/24/outline';

// Helper to render ‚Äú5m ago‚Äù, ‚Äú2h ago‚Äù, etc.
function timeAgo(date) {
  const now = Date.now();
  const seconds = Math.floor((now - date.getTime()) / 1000);
  if (seconds < 60) return `${seconds}s ago`;
  const mins = Math.floor(seconds / 60);
  if (mins < 60) return `${mins}m ago`;
  const hrs = Math.floor(mins / 60);
  if (hrs < 24) return `${hrs}h ago`;
  const days = Math.floor(hrs / 24);
  return `${days}d ago`;
}

// Map action keys to Heroicons
const actionIconMap = {
  user_registered: UserPlusIcon,
  viewed_users: EyeIcon,
  project_created: DocumentDuplicateIcon,
  project_updated: DocumentDuplicateIcon,
  community_enabled: ServerStackIcon,
  project_duplicated: DocumentDuplicateIcon,
};

export default function AdminDashboard() {
  // Stats hook
  const { users, projects, uptime, systemStatus } = useStats();

  // Paginated activity hook (logs default to empty array)
  const [page, setPage] = useState(1);
  const {
    logs: activityLogs = [],
    total = 0,
    loading = false,
  } = usePaginatedActivity(page, 5);
  const totalPages = Math.ceil(total / 5);

  return (
    <div className="p-6">
      {/* Header */}
      <h1 className="text-3xl font-bold text-blue-600 mb-1">
        Welcome, ultamatthew üëã
      </h1>
      <p className="text-gray-600 mb-8 text-sm">
        Last login: May 5, 2025 ‚Äì 11:43 PM
      </p>
      <p className="text-gray-600 mb-8">
        Here‚Äôs a quick overview of your system.
      </p>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-4 gap-6">
        {/* Total Users */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg hover:scale-[1.01] transition duration-200">
          <UserGroupIcon className="h-8 w-8 text-blue-500" />
          <div>
            <p className="text-sm text-gray-500">Total Users</p>
            <p className="text-xl font-bold text-gray-800">{users ?? '‚Äî'}</p>
          </div>
        </div>
        {/* Projects */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg hover:scale-[1.01] transition duration-200">
          <ChartBarIcon className="h-8 w-8 text-indigo-500" />
          <div>
            <p className="text-sm text-gray-500">Projects</p>
            <p className="text-xl font-bold text-gray-800">{projects ?? '‚Äî'}</p>
          </div>
        </div>
        {/* Uptime */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg hover:scale-[1.01] transition duration-200">
          <ClockIcon className="h-8 w-8 text-green-500" />
          <div>
            <p className="text-sm text-gray-500">Backend Uptime</p>
            <p className="text-xl font-bold text-gray-800">{uptime ?? '‚Äî'}</p>
          </div>
        </div>
        {/* System Status */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg hover:scale-[1.01] transition duration-200">
          <ServerStackIcon className="h-8 w-8 text-rose-500" />
          <div>
            <p className="text-sm text-gray-500">System Status</p>
            <p
              className={`text-xl font-bold ${
                systemStatus === 'Operational' ? 'text-green-600' : 'text-red-600'
              }`}
            >
              {systemStatus ?? '‚Äî'}
            </p>
          </div>
        </div>
      </div>

      {/* Recent Activity */}
      <div className="mt-12">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          Recent Activity
        </h2>
        <div className="bg-white rounded-xl shadow p-6">
          {loading ? (
            <p className="text-gray-500">Loading‚Ä¶</p>
          ) : (
            <ul className="divide-y divide-gray-200">
              {activityLogs.length === 0 ? (
                <li className="py-3 text-sm text-gray-500">
                  No recent activity yet.
                </li>
              ) : (
                activityLogs.map((log, idx) => {
                  const Icon = actionIconMap[log.action] || DocumentDuplicateIcon;
                  return (
                    <li key={idx} className="py-3 flex items-start gap-3">
                      <Icon className="h-5 w-5 text-gray-500 flex-shrink-0" />
                      <div className="flex-1">
                        <span className="font-medium text-blue-600">
                          {log.user.username}
                        </span>{' '}
                        <span className="font-semibold">
                          {log.action.replace(/_/g, ' ')}
                        </span>
                        {log.detail && (
                          <span className="text-gray-500">: {log.detail}</span>
                        )}
                        <div className="text-xs text-gray-400 mt-1">
                          {timeAgo(new Date(log.createdAt))}
                        </div>
                      </div>
                    </li>
                  );
                })
              )}
            </ul>
          )}

          {/* Pagination Controls */}
          <div className="flex justify-between items-center mt-4">
            <button
              onClick={() => setPage(p => Math.max(p - 1, 1))}
              disabled={page === 1}
              className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
            >
              Previous
            </button>
            <span className="text-sm text-gray-600">
              Page {page} of {totalPages}
            </span>
            <button
              onClick={() => setPage(p => Math.min(p + 1, totalPages))}
              disabled={page === totalPages}
              className="px-3 py-1 bg-gray-200 rounded disabled:opacity-50"
            >
              Next
            </button>
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="mt-12">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">
          Quick Actions
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
          <button className="bg-blue-600 text-white py-3 px-4 rounded-lg shadow hover:bg-blue-700 transition">
            ‚ûï Create New User
          </button>
          <button className="bg-gray-800 text-white py-3 px-4 rounded-lg shadow hover:bg-gray-900 transition">
            ‚öôÔ∏è Go to System Status
          </button>
          <button className="bg-green-600 text-white py-3 px-4 rounded-lg shadow hover:bg-green-700 transition">
            üìä View Analytics
          </button>
        </div>
      </div>
    </div>
  );
}
