import React, { useEffect, useState } from 'react';

export default function SystemStatus() {
  const [backendStatus, setBackendStatus] = useState('checking...');
  const [backendUp, setBackendUp] = useState(null);

  const BACKEND_URL = 'https://forum-gen-backend-production.up.railway.app/api/auth/ping';

  useEffect(() => {
    const checkBackend = async () => {
      try {
        const res = await fetch(BACKEND_URL);
        if (!res.ok) throw new Error('Not OK');
        const data = await res.json();
        setBackendStatus(`ðŸŸ¢ ${data.message || 'Backend OK'}`);
        setBackendUp(true);
      } catch (err) {
        setBackendStatus('ðŸ”´ Backend down');
        setBackendUp(false);
      }
    };

    checkBackend();

    // Optional: auto-refresh every 15 seconds
    const interval = setInterval(checkBackend, 15000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-2xl font-bold mb-4">System Status</h1>
      <div className="bg-white p-4 rounded shadow max-w-md">
        <p className="text-lg font-medium">Backend Status:</p>
        <p className={backendUp ? 'text-green-600' : 'text-red-600'}>
          {backendStatus}
        </p>
      </div>
    </div>
  );
}
