import React, { useState } from 'react';
import { useStats } from '../hooks/useStats';
import { usePaginatedActivity } from '../hooks/usePaginatedActivity';
import CreateUserModal from '../components/CreateUserModal';
import {
  UserGroupIcon,
  ServerStackIcon,
  ClockIcon,
  ChartBarIcon,
  UserPlusIcon,
  EyeIcon,
  DocumentDuplicateIcon,
} from '@heroicons/react/24/outline';

// timeAgo & actionIconMap same as before…
function timeAgo(date) {
  const now = Date.now();
  const seconds = Math.floor((now - date.getTime()) / 1000);
  if (seconds < 60) return `${seconds}s ago`;
  const mins = Math.floor(seconds / 60);
  if (mins < 60) return `${mins}m ago`;
  const hrs = Math.floor(mins / 60);
  if (hrs < 24) return `${hrs}h ago`;
  return `${Math.floor(hrs / 24)}d ago`;
}
const actionIconMap = {
  user_registered: UserPlusIcon,
  viewed_users: EyeIcon,
  project_created: DocumentDuplicateIcon,
  project_updated: DocumentDuplicateIcon,
  community_enabled: ServerStackIcon,
  project_duplicated: DocumentDuplicateIcon,
};

export default function AdminDashboard() {
  const { users, projects, uptime, systemStatus, refresh: refreshStats } = useStats();

  const [page, setPage] = useState(1);
  const { logs: activityLogs = [], total = 0, loading, refresh: refreshActivity } =
    usePaginatedActivity(page, 5);
  const totalPages = Math.max(Math.ceil(total / 5), 1);

  // Modal state
  const [isModalOpen, setModalOpen] = useState(false);
  const handleUserCreated = () => {
    refreshStats();
    refreshActivity();
  };

  return (
    <>
      <CreateUserModal
        isOpen={isModalOpen}
        onClose={() => setModalOpen(false)}
        onUserCreated={handleUserCreated}
      />

      <div className="p-8 bg-gray-100 min-h-screen">
        {/* Header & Stats Grid… (same as before) */}

        {/* Quick Actions */}
        <div className="mt-12">
          <h2 className="text-xl font-semibold text-gray-800 mb-4">
            Quick Actions
          </h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            <button
              onClick={() => setModalOpen(true)}
              className="bg-blue-600 text-white py-3 px-4 rounded-lg shadow hover:bg-blue-700 transition"
            >
              ➕ Create New User
            </button>
            {/* other actions… */}
          </div>
        </div>

        {/* Recent Activity section… (same as before) */}
      </div>
    </>
  );
}
