import React from 'react';
import {
  UserGroupIcon,
  ServerStackIcon,
  ClockIcon,
  ChartBarIcon,
} from '@heroicons/react/24/outline';

export default function AdminDashboard() {
  return (
    <div>
      {/* Header */}
      <h1 className="text-3xl font-bold text-blue-600 mb-4">Welcome, Admin</h1>
      <p className="text-gray-600 mb-8">Here’s a quick overview of your system.</p>

      {/* Stat Cards Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        {/* Total Users */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg transition">
          <UserGroupIcon className="h-8 w-8 text-blue-500" />
          <div>
            <p className="text-sm text-gray-500">Total Users</p>
            <p className="text-xl font-bold text-gray-800">—</p>
          </div>
        </div>

        {/* Forums */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg transition">
          <ChartBarIcon className="h-8 w-8 text-indigo-500" />
          <div>
            <p className="text-sm text-gray-500">Forums</p>
            <p className="text-xl font-bold text-gray-800">—</p>
          </div>
        </div>

        {/* Uptime */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg transition">
          <ClockIcon className="h-8 w-8 text-green-500" />
          <div>
            <p className="text-sm text-gray-500">Backend Uptime</p>
            <p className="text-xl font-bold text-gray-800">—</p>
          </div>
        </div>

        {/* DB/System Status */}
        <div className="bg-white rounded-xl shadow p-5 flex items-center gap-4 hover:shadow-lg transition">
          <ServerStackIcon className="h-8 w-8 text-rose-500" />
          <div>
            <p className="text-sm text-gray-500">System Status</p>
            <p className="text-xl font-bold text-green-600">Operational</p>
          </div>
        </div>
      </div>

      {/* Later: Recent Activity + Quick Actions */}
    </div>
  );
}
